INSERT INTO affirmations (id, text, gradient_start, gradient_end, is_prompt, is_premium) VALUES
  ('00000000-0000-0000-0010-000000000001', 'I am worthy of all the good in my life', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0010-000000000002', 'I honor who I am at my core', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0010-000000000003', 'I do not need to prove my worth to anyone', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0010-000000000004', 'I am deserving of respect and kindness', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0010-000000000005', 'My value does not decrease based on someone else''s opinion', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0010-000000000006', 'I choose to see my own beauty', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0010-000000000007', 'I am whole and complete as I am', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0010-000000000008', 'I release the need for external validation', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0010-000000000009', 'I am proud of the person I am today', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0010-000000000010', 'I matter simply because I exist', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0010-000000000011', 'I give myself permission to shine', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0010-000000000012', 'I am allowed to take up space in this world', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0010-000000000013', 'My self-worth comes from within', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0010-000000000014', 'I embrace all parts of myself with love', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0010-000000000015', 'I am enough exactly as I am right now', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0010-000000000016', 'I celebrate the unique person I am', '#C17666', '#E8A090', TRUE, TRUE),
  ('00000000-0000-0000-0010-000000000017', 'I trust myself to make good decisions', '#C17666', '#E8A090', TRUE, TRUE),
  ('00000000-0000-0000-0010-000000000018', 'I deserve love and belonging', '#C17666', '#E8A090', TRUE, TRUE),
  ('00000000-0000-0000-0010-000000000019', 'I am worthy of my own compassion', '#C17666', '#E8A090', TRUE, TRUE),
  ('00000000-0000-0000-0010-000000000020', 'I choose to believe in my own greatness', '#C17666', '#E8A090', TRUE, TRUE);
