INSERT INTO affirmations (id, text, gradient_start, gradient_end, is_prompt, is_premium) VALUES
  ('00000000-0000-0000-0013-000000000001', 'My boundaries protect my peace and energy', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0013-000000000002', 'I say no without guilt or explanation', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0013-000000000003', 'I honor my limits and respect my needs', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0013-000000000004', 'Setting boundaries is an act of self-love', '#C17666', '#E8A090', TRUE, FALSE),
