  ('00000000-0000-0000-0013-000000000006', 'I communicate my needs clearly and calmly', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0013-000000000007', 'I release relationships that drain my energy', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0013-000000000008', 'I deserve to feel safe in all my relationships', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0013-000000000009', 'I choose what serves my highest good', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0013-000000000010', 'My feelings are valid and worth expressing', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0013-000000000011', 'I trust myself to know what is right for me', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0013-000000000012', 'I do not owe anyone access to my time or energy', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0013-000000000013', 'I stand firm in my values without apology', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0013-000000000014', 'I attract people who respect my boundaries', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0013-000000000015', 'I am strong enough to walk away from what harms me', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0013-000000000016', 'I give myself permission to rest and recharge', '#C17666', '#E8A090', TRUE, TRUE),
  ('00000000-0000-0000-0013-000000000017', 'I honor my space and my right to peace', '#C17666', '#E8A090', TRUE, TRUE),
  ('00000000-0000-0000-0013-000000000018', 'I let go of what I cannot control', '#C17666', '#E8A090', TRUE, TRUE),
  ('00000000-0000-0000-0013-000000000019', 'My needs matter just as much as anyone else''s', '#C17666', '#E8A090', TRUE, TRUE),
  ('00000000-0000-0000-0013-000000000020', 'I create a life that feels aligned and true', '#C17666', '#E8A090', TRUE, TRUE);
