INSERT INTO affirmations (id, text, gradient_start, gradient_end, is_prompt, is_premium) VALUES
  ('00000000-0000-0000-0015-000000000001', 'I give myself permission to heal at my own pace', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0015-000000000002', 'Every day I am healing a little more', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0015-000000000003', 'I am gentle with myself during difficult times', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0015-000000000004', 'My wounds do not define me', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0015-000000000005', 'I release the pain that no longer serves me', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0015-000000000006', 'I am worthy of rest and recovery', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0015-000000000007', 'I trust my body and mind to heal', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0015-000000000008', 'I choose peace over perfection', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0015-000000000009', 'I am safe to feel my emotions fully', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0015-000000000010', 'I let go of what I cannot change', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0015-000000000011', 'I am stronger than the storms I have survived', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0015-000000000012', 'I deserve compassion from myself and others', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0015-000000000013', 'I am building a life that feels good on the inside', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0015-000000000014', 'I forgive myself for past mistakes', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0015-000000000015', 'I am learning to love the journey of healing', '#C17666', '#E8A090', TRUE, FALSE),
  ('00000000-0000-0000-0015-000000000016', 'I release anger and fill that space with peace', '#C17666', '#E8A090', TRUE, TRUE),
  ('00000000-0000-0000-0015-000000000017', 'I am not broken; I am becoming', '#C17666', '#E8A090', TRUE, TRUE),
  ('00000000-0000-0000-0015-000000000018', 'I allow myself grace through this process', '#C17666', '#E8A090', TRUE, TRUE),
  ('00000000-0000-0000-0015-000000000019', 'I am transforming pain into wisdom', '#C17666', '#E8A090', TRUE, TRUE),
  ('00000000-0000-0000-0015-000000000020', 'I nurture my inner child with love and care', '#C17666', '#E8A090', TRUE, TRUE);
